import React, { useContext } from "react";
import "./ToggleSwitch.css";
import { CurrentTemperatureUnitContext } from "../../contexts/CurrentTemperatureUnitContext";

const ToggleSwitch = () => {
  const { currentTemperatureUnit, handleToggleSwitchChange } = useContext(
    CurrentTemperatureUnitContext
  );

  return (
    <div className="switch">
      <input
        type="checkbox"
        className="switch__box"
        id="toggleSwitch" // Add an id for associating with labels
        onChange={handleToggleSwitchChange}
        checked={currentTemperatureUnit === "C"} // Set checked based on the currentTemperatureUnit
      />
      <label htmlFor="toggleSwitch" className="switch__label">
        <span
          className={
            currentTemperatureUnit === "F"
              ? "switch__slider switch__slider-F"
              : "switch__slider switch__slider-C"
          }
        ></span>
      </label>
      <p
        className={`switch__temp-F ${
          currentTemperatureUnit === "F" ? "switch__active" : "switch__none"
        }`}
      >
        F
      </p>
      <p
        className={`switch__temp-C ${
          currentTemperatureUnit === "C" ? "switch__active" : "switch__none"
        }`}
      >
        C
      </p>
    </div>
  );
};

export default ToggleSwitch;
