import React from "react";
import "./Profile.css";
import avatar from "../../images/avatar.svg";
import ClothesSection from "../ClothesSection/ClothesSection";
const Profile = ({
  onCreateModal,
  onCardClick,
  clothingArr,
  handleLogoutClick,
  handleEditProfileClick,
}) => {
  const { currentUser } = useContext(CurrentUserContext);
  return (
    <section className="profile">
      <div className="profile__sidebar">
        <div className="profile__sidebar-user-info">
          <div className="profile__sidebar-image-container">
            {currentUser.avatar ? (
              <img
                src={currentUser.avatar}
                alt="user avatar"
                className="profile__sidebar-user-avatar"
              ></img>
            ) : (
              getInitials(currentUser.name)
            )}
          </div>
          <h3 className="profile__sidebar-username">
            {currentUser ? currentUser.name : "User Profile"}
          </h3>
        </div>
        <div className="profile__sidebar-options">
          <button
            className="profile__sidebar-button"
            type="button"
            onClick={handleEditProfileClick}
          >
            Change Profile Data
          </button>
          <button
            className="profile__sidebar-button"
            type="button"
            onClick={handleLogoutClick}
          >
            Log Out
          </button>
        </div>
      </div>
      <ClothesSection onCreateModal={onCreateModal} onCardClick={onCardClick} />
    </section>
  );
};
export default Profile;
